import React, { useState } from "react";
import styles from "./Login.module.scss";
import { Link } from "react-router-dom";

export default function Login({ dbParent, dbChildren }) {
  // React States
  const [logedInUser, setLogedInUser] = useState({ userName: "", password: "" , accountId:""});
  const [errorMessages, setErrorMessages] = useState({name:"" , message:""});
  const [isAdult, setIsAdult] = useState(false);
  const [isChild, setIsChild] = useState(false);

   const errors = {
    uname: "invalid username",
    pass: "invalid password",
  };

  const handleSubmit = (event) => {
    //Prevent page reload
    event.preventDefault();

    // Find user login info
    const userParent = dbParent.parents.find(
      (account) => account.userName === logedInUser.userName
    );
    const userChild = dbChildren.children.find(
      (account) => account.userName === logedInUser.userName
    );

    // Compare user info with parents info 
    if (userParent) {
        {console.log(userParent);}
        if (userParent.password !== logedInUser.password) {
          // Invalid password
          setErrorMessages({ name: "pass", message: errors.pass});
        } else {
          setIsAdult(true);
          setLogedInUser((previousState) => {
            return { ...previousState, accountId: userParent.accountId };
          })
        }
      } else {
        if (userChild) {
            if (userChild.password !== logedInUser.password) {
              // Invalid password
              setErrorMessages({ name: "pass", message: errors.pass});
            } else {
              setIsChild(true);
              setLogedInUser((previousState) => {
                return { ...previousState, accountId: userChild.accountId };
              })
            }
          } else {
           
        // Username not found
        setErrorMessages({ name: "uname", message: errors.uname });
      }
    }
    // Compare user info with parents info 

    
  };

  // Generate JSX code for error message
  const renderErrorMessage = (name) =>
    name === errorMessages.name && (
      <div className={styles.error}>{errorMessages.message}</div>
    );

  // JSX code for login form
  const renderForm = (
    <div className={styles.form}>
      <form onSubmit={handleSubmit}>
        <div className={styles.inputContainer}>
          <label>Username </label>
          <input
            type="text"
            name="uname"
            onChange={(event) =>
              setLogedInUser((previousState) => {
                return { ...previousState, userName: event.target.value };
              })
            }
            required
          />
          {renderErrorMessage("uname")}
        </div>
        <div className={styles.inputContainer}>
          <label>Password </label>
          <input
            type="password"
            name="pass"
            onChange={(event) =>
              setLogedInUser((previousState) => {
                return { ...previousState, password: event.target.value };
              })
            }
            required
          />
          {renderErrorMessage("pass")}
        </div>
        <div className={styles.buttonContainer}>
          <input type="submit" />
        </div>
      </form>
    </div>
  );

  return (
    <div className={styles.app}>
        <div className={styles.loginForm}>
        <div className={styles.title}>Sign In</div>
        {isAdult ? (
            <Link to={{ pathname: "/Adults" }}>
             
              <button className={styles.ParentButton} onClick={()=>localStorage.setItem('accountId', logedInUser.accountId)}>välkommen <b> {logedInUser.userName.toUpperCase ()}</b> klicka för att se din sida </button>
            </Link>
        ) : isChild ? (
            <Link to={{ pathname: "/Children" }}>
             
            <button className={styles.childButton}  onClick={()=>localStorage.setItem('accountId', logedInUser.accountId)}>välkommen <b>{logedInUser.userName.toUpperCase ()}</b> klicka för att se din sida </button>
          </Link>
        ) : (
          renderForm
        )}
      </div>
    </div>
  );
}
