import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Loader from './components/Loader/Loader';
import Adults from './pages/Adults';
import Children from './pages/Children';
import Home from './pages/Home';

function App() {

  // should be in in plural i.e. dbAccounts, dbParents? 
  // props should be dbAccounts.accounts, dbChildren.children, dbParents.parents??

  const [newBalance, setNewBalance] = useState(balance);
  console.log(newBalance);
  const [dbAccount, setDbAccount] = useState({

    "accounts": [
      {
        "accountId": 1,
        "parentId": [1],
        "childId": [1]
      }
    ]
  })

  const [dbChildren, setDbChildren] = useState({

    "children": 
      [
        {
          "accountId": 1,
          "childId": 1,
          "userName": "theo",
          "password": "testtheo",
          "pocketMoney": 30,
          "balance": 100,
          "goalTitle": "MineCraft",
          "goalAmount": 50,
          "goalPic": "https://www.kalaskungen.com/pub_images/original/PA424.jpg"
        },
        {
          "accountId": 1,
          "childId": 2,
          "userName": "maxine",
          "password": "testmaxine",
          "pocketMoney": 30,
          "balance": 200,
          "goalTitle": "",
          "goalAmount": 0,
          "goalPic": ""
        },
        {
          "accountId": 2,
          "childId": 3,
          "userName": "vera",
          "password": "testvera",
          "pocketMoney": 50,
          "balance": 500,
          "goalTitle": "",
          "goalAmount": 0,
          "goalPic": ""
        }
      ]
  });

  const [dbParent, setDbParent] = useState({

    "parents": [
      {
        "accountId": 1,
        "parentId": 1,
        "userName": "maria",
        "password": "testmaria"
      }, {
        "accountId": 1,
        "parentId": 2,
        "userName": "ulf",
        "password": "testulf"
      }
    ]
  })

  return (

    <Router>
      <Switch>
        <Route exact path="/">
          <Home />
        </Route>
        <Route exact path="/Children">
          <Children dbAccount={dbAccount} dbChildren={dbChildren}/>
        </Route>
        <Route exact path="/Adults">
          <Adults dbAccount={dbAccount} dbParent={dbParent} dbChildren={dbChildren}/>
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
