import React, { useState } from "react";
import styles from "./Login.module.scss";

export default function Login({ dbParent, dbChildren }) {
  // React States
  const [logedUser, setLogedUser] = useState({ UserName: "", password: "" });

  const [errorMessages, setErrorMessages] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isAdult, setIsAdult] = useState(false);
  const [isChild, setIsChild] = useState(false);

  // User Login info
  const database = [
    {
      username: "user1",
      password: "pass1",
    },
    {
      username: "user2",
      password: "pass2",
    },
  ];

  const errors = {
    uname: "invalid username",
    pass: "invalid password",
  };

  const handleSubmit = (event) => {
    //Prevent page reload
    event.preventDefault();

    // Find user login info
    const userParent = dbParent.parents.find(
      (account) => account.userName === logedUser.UserName
    );
    const userChild = dbChildren.children.find(
        (account) => account.userName === logedUser.UserName
      );

    console.log("userParent", userParent);
    // Compare user info
    if (userParent) {
      if (userParent.password !== logedUser.password) {
        // Invalid password
        setErrorMessages({ name: "pass", message: errors.pass });
      } else {
        setIsAdult(true);
      }
    } else {
      // Username not found
      setErrorMessages({ name: "uname", message: errors.uname });
    }
    if (userChild) {
        if (userChild.password !== logedUser.password) {
          // Invalid password
          setErrorMessages({ name: "pass", message: errors.pass });
        } else {
          setIsChild(true);
        }
      } else {
        // Username not found
        setErrorMessages({ name: "uname", message: errors.uname });
      }
  };

  // Generate JSX code for error message
  const renderErrorMessage = (name) =>
    name === errorMessages.name && (
      <div className={styles.error}>{errorMessages.message}</div>
    );

  // JSX code for login form
  const renderForm = (
    <div className={styles.form}>
      <form onSubmit={handleSubmit}>
        <div className={styles.inputContainer}>
          <label>Username </label>
          <input
            type="text"
            name="uname"
            onChange={(event) =>
              setLogedUser((previousState) => {
                return { ...previousState, UserName: event.target.value };
              })
            }
            required
          />
          {renderErrorMessage("uname")}
        </div>
        <div className={styles.inputContainer}>
          <label>Password </label>
          <input
            type="password"
            name="pass"
            onChange={(event) =>
              setLogedUser((previousState) => {
                return { ...previousState, password: event.target.value };
              })
            }
            required
          />
          {renderErrorMessage("pass")}
        </div>
        <div className={styles.buttonContainer}>
          <input type="submit" />
        </div>
      </form>
    </div>
  );

  return (
    <div className={styles.app}>
      {console.log(logedUser)}

      <div className={styles.loginForm}>
        <div className={styles.title}>Sign In</div>
        {isAdult ? <div><Link to={{pathname: '/Adults'}}/></div> :isChild ? <div>User is child and successfully logged in</div> : renderForm }
        
        
                               
      </div>
    </div>
  );
}
