import React, {useState} from 'react'
import styles from './Login.module.scss'
import {Link} from 'react-router-dom'

export default function Login({dbParent,dbChildren}) {
   
       // React States
  const [logedInUser,setLogedInUser]=useState({userName: '', password: '', accountId: ''});
  const [errorMessages, setErrorMessages] = useState({});
  const [isChild, setIsChild] = useState(false);
  const [isAdult,setIsAdult] = useState(false);

  

  const handleSubmit = (event) => {
    //Prevent page reload
    event.preventDefault();

 

    // Find user login info
    const userChild = dbChildren.children.find((user) => user.userName === logedInUser.userName);
    const userParent = dbParent.parents.find((user) => user.userName === logedInUser.userName);
    {console.log('dbParent',dbParent.parents)}
    {console.log('dbChildren',dbChildren.children)}

{console.log(userChild)}
{console.log(userParent)}
console.log('logedInUser',logedInUser);

    // Compare user info
    if (userChild) {
      if (userChild.password !== logedInUser.password) {
        // Invalid password
        setErrorMessages({ name: "pass", message: errors.pass });
      } else {
        setIsChild(true);
        setLogedInUser(previousState=>{return{...previousState,accountId:userChild.accountId}})
      }
    } else {
     
      if (userParent) {
        if (userParent.password !== logedInUser.password) {
          // Invalid password
          setErrorMessages({ name: "pass", message: errors.pass });
        } else {
            setIsAdult(true);
        }
      }
       else {
        // Username not found
        setErrorMessages({ name: "uname", message: errors.uname });
      }
    }
    



  };

  // Generate JSX code for error message
  const renderErrorMessage = (name) =>
    name === errorMessages.name && (
      <div className={styles.error}>{errorMessages.message}</div>
    );

  // JSX code for login form
  const renderForm = (
    <div className={styles.form}>
      <div className={styles.title}>Sign In</div>
      <form onSubmit={handleSubmit}>
        <div className={styles.inputContainer}>
          <label>Username </label>
          <input type="text" name="uname"onChange={(event)=>setLogedInUser(previousState=>{return{...previousState,userName:event.target.value}})} required />
          {renderErrorMessage("uname")}
        </div>
        <div className={styles.inputContainer}>
          <label>Password </label>
          <input type={styles.password} name="pass"onChange={(event)=>setLogedInUser(previousState=>{return{...previousState,password:event.target.value}})} required />
          {renderErrorMessage("pass")}
        </div>
        <div className={styles.buttonContainer}>
          <input type="submit" />
        </div>
      </form>
    </div>
  );

  return (
    <div className={styles.app}>

      <div className={styles.loginForm}>
        {isChild ? 
                <Link to="/Children" >
                    <button onClick={()=>localStorage.setItem('accountId',  logedInUser. accountId)}> Välkommen {dbChildren.children.userName} </button>  
                </Link> :
                
                
                renderForm}
              { /* {isAdult ? 
                <Link to="/Adults" >
                    <button onClick={()=>localStorage.setItem('accountId',  logedInUser. accountId)}> Välkommen {dbChildren.children.userName} </button>  
              </Link> : renderForm}*/}
      </div>
    </div>
  );
    
}
