import React, {useState} from 'react'
import styles from './Login.module.scss'
import {Link} from 'react-router-dom'

export default function Login({dbParent,dbChildren}) {
   
       // React States
  const [logedInUser,setnewmembersDetails]=useState({name: '', estTime: '', issueId: ''});
  const [errorMessages, setErrorMessages] = useState({});
  const [isChild, setIsChild] = useState(false);
  const [isAdult,setIsAdult] = useState(false);

  // User Login info
  const database = [
    {
      username: "user1",
      password: "pass1"
    },
    {
      username: "user2",
      password: "pass2"
    }
  ];

  const errors = {
    uname: "invalid username",
    pass: "invalid password"
  };

  const handleSubmit = (event) => {
    //Prevent page reload
    event.preventDefault();

    var { uname, pass } = document.forms[0];

    // Find user login info
    const userData = database.find((user) => user.username === uname.value);

    // Compare user info
    if (userData) {
      if (userData.password !== pass.value) {
        // Invalid password
        setErrorMessages({ name: "pass", message: errors.pass });
      } else {
        setIsSubmitted(true);
      }
    } else {
      // Username not found
      setErrorMessages({ name: "uname", message: errors.uname });
    }
  };

  // Generate JSX code for error message
  const renderErrorMessage = (name) =>
    name === errorMessages.name && (
      <div className={styles.error}>{errorMessages.message}</div>
    );

  // JSX code for login form
  const renderForm = (
    <div className={styles.form}>
      <div className={styles.title}>Sign In</div>
      <form onSubmit={handleSubmit}>
        <div className={styles.inputContainer}>
          <label>Username </label>
          <input type="text" name="uname" required />
          {renderErrorMessage("uname")}
        </div>
        <div className={styles.inputContainer}>
          <label>Password </label>
          <input type={styles.password} name="pass" required />
          {renderErrorMessage("pass")}
        </div>
        <div className={styles.buttonContainer}>
          <input type="submit" />
        </div>
      </form>
    </div>
  );

  return (
    <div className={styles.app}>

      <div className={styles.loginForm}>
        {isChild ? 
                <Link to="/Children" >
                    <button onClick={()=>localStorage.setItem('accountId',  logedInUser. accountId)}> Välkommen {dbChildren.children.userName} </button>  
                </Link> : renderForm}
                {isAdult ? 
                <Link to="/Adults" >
                    <button onClick={()=>localStorage.setItem('accountId',  logedInUser. accountId)}> Välkommen {dbChildren.children.userName} </button>  
                </Link> : renderForm}
      </div>
    </div>
  );
    
}
